#!/bin/bash
downloadFileName=certs.h
caFileName=cas.pem
curl https://raw.githubusercontent.com/nodejs/node/master/src/node_root_certs.h -o $downloadFileName
tail -n +2 $downloadFileName | head -n -1 > $caFileName
sed -i 's/"//g' $caFileName
sed -i 's/\\n//g' $caFileName
sed -i 's/,//g' $caFileName
sed -i 's|/\*[^/]*\*/||g' $caFileName
sed -i '/^$/d' $caFileName
